package com.example.lazaapp.detail

import android.os.Bundle
import android.view.View
import com.example.lazaapp.base.BaseFragment
import com.example.lazaapp.databinding.FragmentDetailBinding

class DetailFragment : BaseFragment<FragmentDetailBinding>(FragmentDetailBinding::inflate) {

    private val imageAdapter = ImageAdapter()

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        binding.viewPagerDetail.adapter = imageAdapter
        binding.dotsIndicator.attachTo(binding.viewPagerDetail)

        val list = arrayListOf(
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"

        )
        imageAdapter.updateList(list)
    }


}